import{_ as t,o as r,c as d,a7 as a}from"./chunks/framework.euTsEaZO.js";const f=JSON.parse('{"title":"大阪大学备考计划","description":"python学习日志","frontmatter":{"title":"大阪大学备考计划","description":"python学习日志","sidebar":false,"tag":["个人日志"]},"headers":[],"relativePath":"tools/plan.md","filePath":"tools/plan.md","lastUpdated":null}'),e={name:"tools/plan.md"},o=a('<h3 id="情报学要求" tabindex="-1">情报学要求 <a class="header-anchor" href="#情报学要求" aria-label="Permalink to &quot;情报学要求&quot;">​</a></h3><p><a href="https://www.osaka-u.ac.jp/ja/admissions" target="_blank" rel="noreferrer">入学情报</a></p><p><a href="../public/cs_admission.pdf">选拔要求-工学部（pdf）</a></p><table><thead><tr><th><strong>类别</strong></th><th><strong>详情</strong></th></tr></thead><tbody><tr><td><strong>学部</strong></td><td>工学部</td></tr><tr><td><strong>学科</strong></td><td>情报科学</td></tr><tr><td><strong>申请要求</strong></td><td>- 需要参加大学入学共通考试</td></tr><tr><td></td><td>- 需要通过个别学力测试</td></tr><tr><td><strong>要求科目</strong></td><td>- 国语、地理历史、公民、理科、数学、外国语、情报科学</td></tr><tr><td></td><td>- 具体科目包括：数学（I、II、III、A、B、C），理科（物理、化学、生物），外国语（英语、德语、法语）</td></tr><tr><td><strong>分数配比</strong></td><td>- 大学入学共通考试：325分</td></tr><tr><td></td><td>- 个别学力测试：700分</td></tr><tr><td><strong>考试日期</strong></td><td>- 2025年2月25日</td></tr><tr><td></td><td>- 考试科目：数学、英语、理科</td></tr><tr><td><strong>选拔过程</strong></td><td>- 如果志愿人数超过招生人数的约三倍，将进行初步选拔。</td></tr><tr><td></td><td>- 最终选拔根据分数高低进行录取。</td></tr><tr><td><strong>第二志愿选项</strong></td><td>- 申请者可以选择同一学部内的第二志愿学科。</td></tr><tr><td></td><td>- 第二志愿的选择不会影响第一志愿的结果。</td></tr><tr><td><strong>考试地点</strong></td><td>- 大阪大学，工学部大楼，吹田校区</td></tr><tr><td><strong>学科分配</strong></td><td>- 具体的课程和学科分配在第二或第三学年进行，具体年级取决于学科。</td></tr><tr><td></td><td>- 申请时只能选择学科，最终的课程和学科分配会在之后确定。</td></tr></tbody></table><h3 id="外国人出愿资格" tabindex="-1"><a href="../public/foregin_information.pdf">外国人出愿资格</a>： <a class="header-anchor" href="#外国人出愿资格" aria-label="Permalink to &quot;[外国人出愿资格](../public/foregin_information.pdf)：&quot;">​</a></h3><ul><li>申请者必须具有日本国籍或日本的永住权，并且在外国连续接受了至少两年以上的教育。</li><li>申请者需要满足以下条件之一（共11个条件）才能申请：</li><li>在外国教育体系的高中阶段学习了两年以上，并将在2023年4月1日至2025年3月31日之间毕业或预期毕业。</li><li>在外国教育体系中完成12年教育课程，或通过认证考试取得相应资格。</li><li>在外国取得国际认可的高中毕业资格（如国际文凭、德国的Abitur、法国的Baccalauréat、英国的A-Level等）。</li><li>申请理学部、工学部及基礎工学部的学生需提前参加TOEFL iBT考试，并提交成绩，理学部要求至少61分。</li></ul><h3 id="计划" tabindex="-1">计划 <a class="header-anchor" href="#计划" aria-label="Permalink to &quot;计划&quot;">​</a></h3><div class="timeline-dot"><span class="timeline-dot-title">2024-09-27</span><ul><li><strong>离职</strong></li><li>备考托福</li></ul></div><div class="timeline-dot"><span class="timeline-dot-title">2024-11-30</span><p>托福出分</p></div><h3 id="经验贴" tabindex="-1">经验贴 <a class="header-anchor" href="#经验贴" aria-label="Permalink to &quot;经验贴&quot;">​</a></h3><p><a href="https://zhuanlan.zhihu.com/p/716907936" target="_blank" rel="noreferrer">超详细大阪大学情报学研究科合格经验</a></p>',11),n=[o];function s(i,l,h,p,c,g){return r(),d("div",null,n)}const u=t(e,[["render",s]]);export{f as __pageData,u as default};
